export function interpolateTrig(a, b, t) {
    let tsin = 1 - Math.cos(Math.PI * t / 2.0);
    return a + tsin * (b - a);
}

export function interpolate(a, b, fraction) {
    return a + fraction * (b - a);
}

export function randint(a, b) {
    return (a + Math.floor(Math.random() * (b - a)));
}

export function randRange(a, b) {
    return (a + Math.random() * (b - a));
}

// Flip a coin weighted p for True
export function coinFlip(p) {
    return (Math.random() < p);
}

export function stoc(n) {
    var fn = Math.floor(n);
    var d = coinFlip(n - fn);
    if (d == true) {
        return fn + 1;
    } else {
        return fn;
    }
}

var runningFactorial = [];
var factorial = 1;
for (var i = 0; i < 10; i++) {
    factorial *= i + 1;
    runningFactorial.push(factorial);
}

export function getFactorial(n) {
    if (n <= 0) {
        return 1;
    } else if (n > 10) {
        return runningFactorial[n - 1];
    } else {
        var soln = 1;
        for (var i = 0; i < n; i++) {
            soln *= i + 1;
        }
        return soln;
    }
}

export function getDoubleFactorial(n) {
    if (n <= 0) {
        return 1;
    } else {
        var soln = 1;
        for (var i = 0; i < n; i++) {
            if (i + 1 % 2 == n % 2) {
                soln *= i + 1;
            }
        }
        return soln;
    }
}

export function choose(n, k) {
    return getFactorial(n) / (getFactorial(n - k) * getFactorial(k));
}

export function mapToSpherical(x, y, z) {
    var rho = sqrt(x * x + y * y + z * z);
    var theta = atan(y / x);
    var phi = acos(z / rho);
    return [rho, theta, phi];
}

export function getNormalization(n, l) {
    var constTerm = sqrt(pow(2 / (n * A0), 3) * getFactorial(n - l - 1) / (2 * n * getFactorial(n + l)));
    var radius = (rho) => (2 * rho) / (n * A0);
    return (rho) => constTerm * exp(radius(rho) / -2) * pow(radius(rho), l);
}


export function getLaguerrePolynomial(n, l) {
    var kl = 2 * l + 1;
    var nl = n - l - 1;

    return (rho) => {
        var signedPower = 1;
        var total = 0;
        for (var i = 0; i <= n; i++) {
            total += signedPower * getFactorial(nl + kl) / (getFactorial(nl - i) * getFactorial(kl + i) * getFactorial(i));
            signedPower *= -rho;
        }
        var constTerm = sqrt(pow(2 / (n * A0), 3) * getFactorial(n - l - 1) / (2 * n * getFactorial(n + l)));
        var radius = (2 * rho) / (n * A0);
        return total * constTerm * exp(radius / -2) * pow(radius, l);
    }
}

export function getLegendrePolynomial(l, m) {
    var sign = m % 2 == 0 ? 1 : -1;
    return (rho) => {
        var base = sign * getDoubleFactorial(2 * m - 1) * pow(1 - rho * rho, m / 2);
        var nextBase = (2 * m + 1) * rho * base;
        if (l - m == 0) {
            return base;
        } else if (l - m == 1) {
            return nextBase;
        } else if (l - m >= 2) {
            var temp = m;
            for (var i = 0; i < l - m - 1; i++) {
                var nextPolynomial = ((2 * l + 1) * nextBase - (l + m) * base) / (l - m + 1);
                base = nextBase;
                nextBase = nextPolynomial;
            }
            return nextPolynomial;
        }
    }
}

export function getSphericalHarmonic(l, m) {
    var sign = complex(m % 2 == 0 ? 1 : -1, 0);
    var normalization = complex(sqrt((getFactorial(l - m) * (2 * l + 1)) / (getFactorial(l + m) * 4 * Math.PI)), 0);
    var complexTerm = (phi) => exp(complex(0, m * phi));

    return (phi) => multiply(sign, normalization, complexTerm(phi));
}
