import { coinFlip, randRange, } from '../../../utils/functions';
import { complex, sqrt, exp, atan, sin, cos, acos, pow, add, subtract, multiply, divide } from 'mathjs';


const A0 = 1;

function getFactorial(n) {
    var soln = 1;
    for (var i = 0; i < n; i++) {
        soln *= i + 1;
    }
    return soln;
}

function mapToSpherical(x, y, z) {
    var rho = sqrt(x * x + y * y + z * z);
    var theta = atan(y / x);
    var phi = acos(z / rho);
    return [rho, theta, phi];
}

function getNormalization(n, l) {
    var constTerm = sqrt(pow(2 / (n * A0), 3) * getFactorial(n - l - 1) / (2 * n * getFactorial(n + l)));
    var radius = (rho) => (2 * rho) / (n * A0);
    return (rho) => constTerm * exp(radius(rho) / -2) * pow(radius(rho), l);
}


function getLaguerrePolynomial(n, l) {
    var a = 2 * l + 1;
    var k = n - l - 1;
    var memoTable = Array(n);
    memoTable[0] = (rho) => 1;
    memoTable[1] = (rho) => (1 + a - rho);
    for (var i = 2; i < n; i++) {
        memoTable[i] = (rho) => {
            var leftTerm = (2 * k + a - rho) * memoTable[i - 1](rho);
            var rightTerm = (k - a - 1) * memoTable[i - 2](rho);
            return (leftTerm - rightTerm) / k;
        }
    }
    return (rho) => memoTable[n - 1](rho) * getNormalization(n, l)(rho);
}

function getLegendrePolynomial(l, m) {
    var memoTable = Array(l);
    memoTable[0] = (x) => 1;
    memoTable[1] = (x) => x;
    for (var i = 2; i < l; i++) {
        memoTable[i] = (x) => {
            var leftTerm = x * (2 * l - 1) * memoTable[i - 1](x);
            var rightTerm = (l + m - 1) * memoTable[i - 2](x);
            return (leftTerm - rightTerm) / (l - m);
        }
    }
    return memoTable[l - 1];
}

function getSphericalHarmonic(l, m) {
    var sign = complex(m % 2 == 0 ? 1 : -1, 0);
    var normalization = complex(sqrt((getFactorial(l - m) * (2 * l + 1)) / (getFactorial(l + m) * 4 * Math.PI)), 0);
    var complexTerm = (phi) => exp(complex(0, m * phi));

    return (phi) => multiply(sign, normalization, complexTerm(phi));
}

function getFunctional(n, l, m) {
    return (x, y, z) => {
        var sphericals = mapToSpherical(x, y, z);
        var rho = sphericals[0];
        var theta = sphericals[1];
        var phi = sphericals[2];

        return multiply(getLaguerrePolynomial(n, l)(rho), getLegendrePolynomial(l, m)(cos(theta)), getSphericalHarmonic(l, m)(phi));
    };
}

function getGradient(n, l, m) {
    var rhoTerm = getLaguerrePolynomial(n, l);
    var delta = 0.01;
    // TODO: Explicitly calculate functions for less computational load
    return (x, y, z) => {
        var sphericals = mapToSpherical(x, y, z);
        var rho = sphericals[0];
        var theta = sphericals[1];
        var phi = sphericals[2];

        var deltaRho = (getLaguerrePolynomial(n, l)(rho + delta / 2) - getLaguerrePolynomial(n, l)(rho - delta / 2)) / (delta);
        var deltaTheta = (getLegendrePolynomial(l, m)(theta + delta / 2) - getLegendrePolynomial(l, m)(theta - delta / 2)) / (delta * rho);
        var deltaPhi = (getSphericalHarmonic(l, m)(phi + delta / 2) - getSphericalHarmonic(l, m)(phi - delta / 2)) / (delta * rho * Math.sin(theta));

        var unitRho = [sin(theta) * cos(phi), sin(theta) * sin(phi), cos(theta)];
        var unitTheta = [cos(theta) * cos(phi), cos(theta) * sin(phi), sin(theta) * -1];
        var unitPhi = [sin(phi) * -1, cos(phi), 0];

        return [
            unitRho[0] * deltaRho + unitTheta[0] * deltaTheta + unitPhi[0] * deltaPhi,
            unitRho[1] * deltaRho + unitTheta[1] * deltaTheta + unitPhi[1] * deltaPhi,
            unitRho[2] * deltaRho + unitTheta[2] * deltaTheta + unitPhi[2] * deltaPhi,
        ];
    }
}

function getEnergyLevel(n) {
    return 1 / (n * n);
}


class Particle {
    constructor(x, y, z) {
        this.x = x;
        this.y = y;
        this.z = z;
    }

    move(dx, dy, dz) {
        this.x += dx;
        this.y += dy;
        this.z = dz;
    }
}


const Atom = class {
    constructor (props) {
        this.superposition = props.superposition;
        this.particleCount = props.particleCount;
        this.boxSize = props.boxSize;

        this.particles = [];
        this.timer = 0;
        while (this.particles.length < props.particleCount) {
            var newParticle = new Particle(
                randRange(props.boxSize * -1, props.boxSize),
                randRange(props.boxSize * -1, props.boxSize),
                randRange(props.boxSize * -1, props.boxSize),
            )
            if (coinFlip(this.getProbabilityDistribution(newParticle.x, newParticle.y, newParticle.z))) {
                this.particles.push(newParticle);
            }
        }
        console.log(this.t);
    }

    getWavefunction(x, y, z, t) {
        var total = 0;
        for (var i = 0; i < this.superposition.length; i++) {
            var phase = exp(complex(0, getEnergyLevel(this.superposition[i].n) * t));

            console.log(t);
            //console.log(getFunctional(this.superposition[i].n, this.superposition[i].l, this.superposition[i].m)(x, y, z));

            total = add(total, multiply(phase, this.superposition[i].weight, getFunctional(this.superposition[i].n, this.superposition[i].l, this.superposition[i].m)(x, y, z)));
        }
        return total;
    }

    getProbabilityDistribution(x, y, z, t) {
        var wavefunction = this.getWavefunction(x, y, z, t);
        var wavefunctionC = complex.conjugate(wavefunction);
        return wavefunction * wavefunctionC;    
    }

    getWaveFunctionGradient(x, y, z, t) {
        var total = [0, 0, 0];
        for (var i = 0; i < this.superposition.length; i++) {
            var phase = exp(complex(0, getEnergyLevel(this.superposition[i].n) * t));
            var grad = phase * this.superposition[i].weight * getGradient(this.superposition[i].n, this.superposition[i].l, this.superposition[i].m)(x, y, z);
            total[0] += grad[0];
            total[1] += grad[1];
            total[2] += grad[2];
        }
        return total;
    }

    getVelocity(x, y, z, t) {
        var wavefunction = this.getWavefunction(x, y, z, t);
        var wavefunctionC = complex.conjugate(wavefunction);
        var gradient = this.getWavefunctionGradient(x, y, z, t);
        var gradientC = [
            complex.conjugate(gradient[0]), 
            complex.conjugate(gradient[1]),
            complex.conjugate(gradient[2]),
        ]
        var velocity = [
            (wavefunction * gradientC[0] - wavefunctionC * gradient[0]) / (wavefunction * wavefunctionC),
            (wavefunction * gradientC[1] - wavefunctionC * gradient[1]) / (wavefunction * wavefunctionC),
            (wavefunction * gradientC[2] - wavefunctionC * gradient[2]) / (wavefunction * wavefunctionC),
        ]
        return velocity;
    }

    update(increment) {
        for (var i = 0; i < this.particles.length; i++) {
            var p = this.particles[i];
            var velocity = this.getVelocity(p.x, p.y, p.z, this.timer);
            p.move(velocity[0], velocity[1], velocity[2]);
        }
        this.timer += increment;

        var p = this.particles[0];
        var velocity = this.getVelocity(p.x, p.y, p.z, this.timer);
        console.log(velocity);
        return this;
    }
}

export default Atom;
