import { coinFlip, 
    randRange, 
    mapToSpherical, 
    getLaguerrePolynomial, 
    getLegendrePolynomial, 
    getSphericalHarmonic,    } from '../../../utils/functions';
import { complex, sqrt, exp, sin, cos, add, subtract, multiply, divide } from 'mathjs';

function getFunctional(n, l, m) {
    return (x, y, z) => {
        var sphericals = mapToSpherical(x, y, z);
        var rho = sphericals[0];
        var theta = sphericals[1];
        var phi = sphericals[2];

        return multiply(getLaguerrePolynomial(n, l)(rho), getLegendrePolynomial(l, m)(cos(theta)), getSphericalHarmonic(l, m)(phi));
    };
}

function getGradient(n, l, m) {

    var delta = 0.01;
    // TODO: Explicitly calculate functions for less computational load
    return (x, y, z) => {
        var sphericals = mapToSpherical(x, y, z);
        var rho = sphericals[0];
        var theta = sphericals[1];
        var phi = sphericals[2];

        var deltaRho = divide(subtract(getLaguerrePolynomial(n, l)(rho + delta / 2), getLaguerrePolynomial(n, l)(rho - delta / 2)), (delta));
        var deltaTheta = divide(subtract(getLegendrePolynomial(l, m)(theta + delta / 2), getLegendrePolynomial(l, m)(theta - delta / 2)), (delta * rho));
        var deltaPhi = divide(subtract(getSphericalHarmonic(l, m)(phi + delta / 2), getSphericalHarmonic(l, m)(phi - delta / 2)), (delta * rho * Math.sin(theta)));

        var unitRho = [sin(theta) * cos(phi), sin(theta) * sin(phi), cos(theta)];
        var unitTheta = [cos(theta) * cos(phi), cos(theta) * sin(phi), sin(theta) * -1];
        var unitPhi = [sin(phi) * -1, cos(phi), 0];

        return [
            add(multiply(unitRho[0], deltaRho), multiply(unitTheta[0], deltaTheta), multiply(unitPhi[0], deltaPhi)),
            add(multiply(unitRho[1], deltaRho), multiply(unitTheta[1], deltaTheta), multiply(unitPhi[1], deltaPhi)),
            add(multiply(unitRho[2], deltaRho), multiply(unitTheta[2], deltaTheta), multiply(unitPhi[2], deltaPhi)),
        ];
    }
}

function getEnergyLevel(n) {
    return 1.0 / (n * n);
}


class Particle {
    constructor(x, y, z) {
        this.x = x;
        this.y = y;
        this.z = z;
    }

    move(dx, dy, dz) {
        this.x += dx;
        this.y += dy;
        this.z += dz;
    }

    set(dx, dy, dz) {
        this.x = dx;
        this.y = dy;
        this.z = dz;
    }
}


const Atom = class {
    constructor (props) {
        this.superposition = props.superposition;
        this.particleCount = props.particleCount;
        this.boxSize = props.boxSize;
        this.timer = 0;

        this.particles = [];

        var probabilityMultiplier = 10000;
        while (this.particles.length < props.particleCount) {
            var newParticle = new Particle(
                randRange(props.boxSize * -1, props.boxSize),
                randRange(props.boxSize * -1, props.boxSize),
                randRange(props.boxSize * -1, props.boxSize),
            );
            if (coinFlip(probabilityMultiplier * this.getProbabilityDistribution(newParticle.x, newParticle.y, newParticle.z, 0))) {
                this.particles.push(newParticle);
            }
        }
    }

    getWavefunction(x, y, z, t) {
        var total = 0;
        for (var i = 0; i < this.superposition.length; i++) {
            var phase = exp(complex(0, getEnergyLevel(this.superposition[i].n) * t));
            total = add(total, multiply(phase, this.superposition[i].weight, getFunctional(this.superposition[i].n, this.superposition[i].l, this.superposition[i].m)(x, y, z)));
        }
        return total;
    }

    getProbabilityDistribution(x, y, z, t) {
        var wavefunction = this.getWavefunction(x, y, z, t);
        var wavefunctionC = wavefunction.conjugate();
        return wavefunction * wavefunctionC;    
    }

    getWavefunctionGradient(x, y, z, t) {
        var total = [0, 0, 0];
        for (var i = 0; i < this.superposition.length; i++) {
            var phase = exp(complex(0, getEnergyLevel(this.superposition[i].n) * t));
            var grad = multiply(phase, this.superposition[i].weight, getGradient(this.superposition[i].n, this.superposition[i].l, this.superposition[i].m)(x, y, z));
            total[0] = add(total[0], grad[0]);
            total[1] = add(total[1], grad[1]);
            total[2] = add(total[2], grad[2]);
        }
        return total;
    }

    getVelocity(x, y, z, t) {
        var wavefunction = this.getWavefunction(x, y, z, t);
        var wavefunctionC = wavefunction.conjugate();
        var gradient = this.getWavefunctionGradient(x, y, z, t);
        var gradientC = [
            gradient[0].conjugate(), 
            gradient[1].conjugate(), 
            gradient[2].conjugate(), 
        ]
        var velocity = [
            divide(subtract(multiply(wavefunction, gradientC[0]), multiply(wavefunctionC, gradient[0])), multiply(wavefunction, wavefunctionC)),
            divide(subtract(multiply(wavefunction, gradientC[1]), multiply(wavefunctionC, gradient[1])), multiply(wavefunction, wavefunctionC)),
            divide(subtract(multiply(wavefunction, gradientC[2]), multiply(wavefunctionC, gradient[2])), multiply(wavefunction, wavefunctionC))
        ]
        return velocity;
    }

    update(increment) {
        var deltat = 0.001;
        var p = this.particles[0];
        for (var i = 0; i < this.particles.length; i++) {
            var p = this.particles[i];
            var velocity = this.getVelocity(p.x, p.y, p.z, this.timer);
            var oldNorm = sqrt(p.x * p.x + p.y * p.y + p.z * p.z);
            p.move(velocity[0].im * deltat, velocity[1].im * deltat, velocity[2].im * deltat);
            var newNorm = sqrt(p.x * p.x + p.y * p.y + p.z * p.z);
            p.set(p.x * oldNorm / newNorm, p.y * oldNorm / newNorm, p.z * oldNorm / newNorm);
        }
        this.timer += 1;
        return this;
    }
}

export default Atom;
