import React, { useState, useEffect } from 'react';
import { complex, sqrt, exp, atan, acos, pow, multiply } from 'mathjs';

import { default as FoldGraphics } from'./fold-graphics/fold-graphics';
import { default as AtomGraphics } from'./fold-graphics/atom-graphics';
import { default as Atom } from './fold-graphics/atom';
import { default as System } from './fold-graphics/system';

const FoldCanvas = ({
    height,
    width,
}) => {

    const [system, setSystem] = useState(new System({}));
    const [atom, setAtom] = useState(new Atom({
        superposition: [
            {weight: 0.707,
                n: 2,
                l: 2,
                m: 1,},
                {weight: 1,
                    n: 2,
                    l: 0,
                    m: 0,},
        ],
        particleCount: 400,
        boxSize: 1.5,
    }));

    const [canvasTimer, setCanvasTimer] = useState(0);

    useEffect(() => {
        const interval = setInterval(() => {
            setAtom(atom.update(1));
            setSystem(system.update(1));
            setCanvasTimer(canvasTimer + 1);
        }, 20);
        return () => clearInterval(interval);
      }, [canvasTimer]);

    return (
        <canvas id="fold-graphics" style={{opacity:0.8}} height={height} width={width}>
            <AtomGraphics atom={atom} />
        </canvas>
    );
};

export default FoldCanvas;
